CREATE TYPE ENUM_USER_ROLE AS ENUM ('authenticated_user', 'administrator', 'moderator', 'banned_user', 'deleted_user');
CREATE TYPE ENUM_VOTE_TYPE AS ENUM ('positive', 'negative');
CREATE TYPE ENUM_SCORE_TYPE AS ENUM ('total_score', 'answer_score', 'question_score');

CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(255) UNIQUE NOT NULL,
    email VARCHAR(255) UNIQUE,
    hashed_password VARCHAR(255),
    bio TEXT,
    signup_date DATE DEFAULT CURRENT_DATE NOT NULL,
    user_role ENUM_USER_ROLE DEFAULT 'authenticated_user' NOT NULL,
    image_url TEXT,
    is_anonymous BOOLEAN DEFAULT FALSE NOT NULL,

    -- check email address
    CONSTRAINT email_format_check CHECK (
        (user_role IN ('authenticated_user', 'banned_user') AND email ~ '^up[0-9]{9}@up\.pt$') -- up + 9 digits + @up.pt
        OR
        (user_role IN ('administrator', 'moderator') AND email ~ '@up\.pt$') -- needs to end with @up.pt
        OR
        (user_role = 'deleted_user' AND email IS NULL)
    )
);

