-- view para conseguirmos ter os scores de todas as questions diretamente e não termos de tar sempre a ir buscar todos os votos
CREATE VIEW questions_scores AS 
SELECT
    questions.id,
    COUNT(CASE WHEN questions_votes.vote_type = 'positive' THEN 1 END) - COUNT(CASE WHEN questions_votes.vote_type = 'negative' THEN 1 END) AS number_of_votes
FROM
    questions
LEFT JOIN -- para incluir mesmo as perguntas que nao tem nenhum voto
    questions_votes ON questions_votes.question_id = questions.id
GROUP BY
    questions.id;


-- view para conseguirmos ter os scores de todas as answers diretamente e não termos de tar sempre a ir buscar todos os votos
CREATE VIEW answers_scores AS 
SELECT
    answers.id,
    COUNT(CASE WHEN answers_votes.vote_type = 'positive' THEN 1 END) - COUNT(CASE WHEN answers_votes.vote_type = 'negative' THEN 1 END) AS number_of_votes
FROM
    answers
LEFT JOIN -- para incluir mesmo as respostas que nao tem nenhum voto
    answers_votes ON answers_votes.answer_id = answers.id
GROUP BY
    answers.id;

-- criar view para o score do user que é baseado nos scores positivos das answers e das perguntas
CREATE VIEW user_scores AS
WITH scores AS (
    SELECT
        users.id AS user_id,
        COALESCE(SUM(CASE WHEN questions_votes.vote_type = 'positive' THEN 1 ELSE 0 END), 0) AS total_question_score,
        COALESCE(SUM(CASE WHEN answers_votes.vote_type = 'positive' THEN 1 ELSE 0 END), 0) AS total_answer_score
    FROM
        users
    LEFT JOIN -- para incluir mesmo os users que nao tem nenhum voto em perguntas
        questions_votes ON users.id = questions_votes.user_id
    LEFT JOIN -- para incluir mesmo os users que nao tem nenhum voto em respostas
        answers_votes ON users.id = answers_votes.user_id
    GROUP BY
        users.id
)
SELECT
    user_id,
    total_question_score,
    total_answer_score,
    total_question_score + total_answer_score AS total_score -- add total_question_score and total_answer_score to get total_score
FROM
    scores;


