-- Add column to store computed ts_vectors in questions table
ALTER TABLE questions
ADD COLUMN tsvectors TSVECTOR;

-- Create a function to automatically update ts_vectors on insert or update
CREATE OR REPLACE FUNCTION question_search_update() RETURNS TRIGGER AS $$
BEGIN
  IF TG_OP = 'INSERT' THEN
    NEW.tsvectors = (
        setweight(to_tsvector('english', NEW.title), 'A') ||
        setweight(to_tsvector('english', NEW.content), 'B')
    );
  END IF;
  IF TG_OP = 'UPDATE' THEN
    IF NEW.title <> OLD.title OR NEW.content <> OLD.content THEN
      NEW.tsvectors = (
        setweight(to_tsvector('english', NEW.title), 'A') ||
        setweight(to_tsvector('english', NEW.content), 'B')
      );
    END IF;
  END IF;
  RETURN NEW;
END $$ LANGUAGE plpgsql;

-- Create trigger before insert or update on questions
CREATE TRIGGER question_search_update
BEFORE INSERT OR UPDATE ON questions
FOR EACH ROW
EXECUTE PROCEDURE question_search_update();

-- Create GIN index for ts_vectors
CREATE INDEX search_idx_questions ON questions USING GIN (tsvectors);


