CREATE OR REPLACE FUNCTION update_badges_question()
RETURNS TRIGGER AS $$
DECLARE
    question_owner_id INT;
BEGIN
    -- get the question owner
    SELECT user_id 
    INTO question_owner_id
    FROM questions
    WHERE id = NEW.question_id;

    -- check and if needed update user_badges for questions (score earned from questions)
    SELECT question_owner_id, badges.id
    FROM badges
    WHERE badges.score_necessary_type = 'question_score'
      AND badges.score_necessary <= (
          SELECT total_question_score FROM user_scores WHERE user_id = question_owner_id
      )
      AND NOT EXISTS (
          SELECT 1 FROM user_badges WHERE user_id = question_owner_id AND badge_id = badges.id
      );

    -- check and if needed update user_badges for total (score earned from questions and answers)
    SELECT question_owner_id, badges.id
    FROM badges
    WHERE badges.score_necessary_type = 'total_score'
      AND badges.score_necessary <= (
          SELECT total_score FROM user_scores WHERE user_id = question_owner_id
      )
      AND NOT EXISTS (
          SELECT 1 FROM user_badges WHERE user_id = question_owner_id AND badge_id = badges.id
      );

    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trigger_question_votes_update_badges
AFTER INSERT OR DELETE OR UPDATE ON questions_votes
FOR EACH ROW
EXECUTE FUNCTION update_badges_question();

CREATE OR REPLACE FUNCTION update_badges_answer()
RETURNS TRIGGER AS $$
DECLARE
    answer_owner_id INT;
BEGIN
    -- get the question owner
    SELECT user_id 
    INTO answer_owner_id
    FROM answers
    WHERE id = NEW.answer_id;

    -- check and if needed update user_badges for answers (score earned from answers)
    SELECT answer_owner_id, badges.id
    FROM badges
    WHERE badges.score_necessary_type = 'answer_score'
      AND badges.score_necessary <= (
          SELECT total_answer_score FROM user_scores WHERE user_id = answer_owner_id
      )
      AND NOT EXISTS (
          SELECT 1 FROM user_badges WHERE user_id = answer_owner_id AND badge_id = badges.id
      );

    -- check and if needed update user_badges for total (score earned from questions and answers)
    SELECT answer_owner_id, badges.id
    FROM badges
    WHERE badges.score_necessary_type = 'total_score'
      AND badges.score_necessary <= (
          SELECT total_score FROM user_scores WHERE user_id = answer_owner_id
      )
      AND NOT EXISTS (
          SELECT 1 FROM user_badges WHERE user_id = answer_owner_id AND badge_id = badges.id
      );

