CREATE OR REPLACE FUNCTION prevent_self_vote_on_question()
RETURNS TRIGGER AS $$
BEGIN
    -- check if the user is the question author
    IF NEW.user_id = (SELECT user_id FROM questions WHERE id = NEW.question_id) THEN
        RAISE EXCEPTION 'User cannot vote on their own question';
    END IF;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trigger_prevent_self_vote_on_question
    BEFORE INSERT ON questions_votes
    FOR EACH ROW
    EXECUTE FUNCTION prevent_self_vote_on_question();

CREATE OR REPLACE FUNCTION prevent_self_vote_on_answer()
RETURNS TRIGGER AS $$
BEGIN
    -- check if the user is the answer author
    IF NEW.user_id = (SELECT user_id FROM answers WHERE id = NEW.answer_id) THEN
        RAISE EXCEPTION 'User cannot vote on their own answer';
    END IF;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trigger_prevent_self_vote_on_answer
    BEFORE INSERT ON answers_votes
    FOR EACH ROW
    EXECUTE FUNCTION prevent_self_vote_on_answer();

